// Name: Comment Blocks
// ID: lmscomments
// Description: Annotate your scripts.
// By: LilyMakesThings <https://scratch.mit.edu/users/LilyMakesThings/>

const icon = 
  'data:image/png;base64,';

class CommentBlocks {
    constructor(runtime) {
    this.runtime = runtime
    this._formatMessage = runtime.getFormatMessage({
      'zh-cn': {
        'CmntExt.extensionName': '注释块',
      },
      en: {
        'CmntExt.extensionName': 'Comment blocks',
      },
    });
  }
  
  formatMessage(id) {
    return this._formatMessage({
      id,
      default: id,
      description: id,
    })
  }
  
    getInfo() {
      return {
        id: "lmscomments",
        name: this.formatMessage('CmntExt.extensionName'),
        color1: "#191e25",
        color2: "#191e25",
        color3: "#191919",
        menuIconURI: icon,
        blocks: [
          {
            opcode: "commentHat",
            blockType: Scratch.BlockType.HAT,
            text: "// [COMMENT]",
            isEdgeActivated: false,
            arguments: {
              COMMENT: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "comment",
              },
            },
          },
          {
            opcode: "commentCommand",
            blockType: Scratch.BlockType.COMMAND,
            text: "//[COMMENT]",
            arguments: {
              COMMENT: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "comment",
              },
            },
          },
          {
            opcode: "commentC",
            blockType: Scratch.BlockType.CONDITIONAL,
            text: "//[COMMENT]",
            arguments: {
              COMMENT: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "comment",
              },
            },
          },
          {
            opcode: "commentReporter",
            blockType: Scratch.BlockType.REPORTER,
            text: "[INPUT] // [COMMENT]",
            allowDropAnywhere: true,
            arguments: {
              COMMENT: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "comment",
              },
              INPUT: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "",
              },
            },
          },
          {
            opcode: "commentBoolean",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "[INPUT] // [COMMENT]",
            arguments: {
              COMMENT: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "comment",
              },
              INPUT: {
                type: Scratch.ArgumentType.BOOLEAN,
              },
            },
          },
        ],
      };
    }

    commentHat() {
      return true
    }

    commentCommand() {
      // no-op
    }

    commentC(args, util) {
      util.startBranch(1, false);
    }

    commentReporter(args) {
      return args.INPUT;
    }
  
    commentBoolean(args) {
      return args.INPUT || false;
    }
  }
  
  window.tempExt = {
    Extension: CommentBlocks,
    info: {
      name: 'CmntExt.extensionName',
      description: 'CmntExt.description',
      extensionId: 'lmscomments',
      iconURL: "https://extensions.turbowarp.org/images/Lily/CommentBlocks.svg",
      insetIconURL: icon,
      featured: true,
      disabled: false,
      collaborator: 'Lily@TW',
      collaboratorURL: 'https://scratch.mit.edu/users/lilymakesthings/',
      collaboratorList: [
              {
                  collaborator: 'Lily@TW',
                  collaboratorURL: 'https://scratch.mit.edu/users/lilymakesthings/'
              },
          ]
    },
    l10n: {
      'zh-cn': {
        'CmntExt.extensionName': "注释块",
        'CmntExt.description': '注释你的脚本',
      },
      'en': {
        'CmntExt.extensionName': "Comment blocks",
        'CmntExt.description': 'Annotate your scripts',
      },
    },
  };
