    /**
     * è·å–ä½œå“åˆ—è¡¨ï¼Œä»¥åŠé”®åˆ—è¡¨ï¼ˆç›¸å½“äºæ•´ä¸ªæ•°æ®åº“ï¼‰
     * @returns {Promise<{[uuid: string]: string[]}>}
     */
    async kProjectListAsync() {
        if (this.db === undefined) {
            console.warn("æ•°æ®åº“æ²¡æœ‰è¢«æ‰“å¼€ã€‚");
            return {};
        }
        /** @type { {[uuid: string]: string[]} } */
        const ret = {};
        const transaction = this.db.transaction(['indexeddb-store'], "readonly");
        const objectStore = transaction.objectStore('indexeddb-store');
        const request = objectStore.openCursor();
        let cursor = null;
        while ((cursor = await this.dbReqAsync(request)) !== null) {
            /** @type {DBKeyInfo | DBProjectInfo} */
            const val = cursor.value;
            if (val.name !== 0) {
                const valuuid = ret[val.uuid];
                if (valuuid === undefined) {
                    ret[val.uuid] = [val.name];
                } else {
                    valuuid.push(val.name);
                }
            }
            cursor.continue();
            // continue åéœ€è¦å† await ä¸€æ¬¡ request æ¥è·å–æ–°çš„ cursor
        }
        return ret;
    }

