        const transaction = this.db.transaction(['indexeddb-store'], "readwrite");
        const objectStore = transaction.objectStore('indexeddb-store');
        const request = objectStore.index('uuid').openCursor(uuid);
        /** @type {Promise<void>[]} */
        const deletePromises = [];
