//È¢úËâ≤ËΩ¨Êç¢
String.prototype.colorHex = function () {
    // RGBÈ¢úËâ≤ÂÄºÁöÑÊ≠£Âàô
    var reg = /^(rgb|RGB)/;
    var color = this;
    if (reg.test(color)) {
        var strHex = "#";
        // ÊääRGBÁöÑ3‰∏™Êï∞ÂÄºÂèòÊàêÊï∞ÁªÑ
        var colorArr = color.replace(/(?:\(|\)|rgb|RGB)*/g, "").split(",");
        // ËΩ¨Êàê16ËøõÂà∂
        for (var i = 0; i < colorArr.length; i++) {
            var hex = Number(colorArr[i]).toString(16);
            if (hex === "0") {
                hex += hex;
            }
            strHex += hex;
        }
        return strHex;
    } else {
        return String(color);
    }
};

//ÈîÆÁõò‰∫ã‰ª∂ÁõëÂê¨
document.addEventListener("keydown", keydown);
document.addEventListener("keyup", keyup);
function keydown(event) {
    keypress[event.code] = true;
    lastKey = event.code;
}
function keyup(event) {
    delete keypress[event.code];
}
//ÊªöËΩÆ‰∫ã‰ª∂ÁõëÂê¨
var scrollFunc = e => {
    e = e || window.event;
    if (e.wheelDelta) {  //Âà§Êñ≠ÊµèËßàÂô®IEÔºåË∞∑Ê≠åÊªëËΩÆ‰∫ã‰ª∂
        MouseWheel = e.wheelDelta;
    } else if (e.detail) {  //FirefoxÊªëËΩÆ‰∫ã‰ª∂
        MouseWheel = e.detail;
    }
    clearTimeout(timer);
    timer = setTimeout(() => {
        MouseWheel = 0;
    }, 30);
};
//ÁªôÈ°µÈù¢ÁªëÂÆöÊªëËΩÆÊªöÂä®‰∫ã‰ª∂
if (cvs.parentNode.addEventListener) { //ÁÅ´Áãê‰ΩøÁî®DOMMouseScrollÁªëÂÆö
    cvs.parentNode.addEventListener('DOMMouseScroll', scrollFunc, false);
}
//ÂÖ∂‰ªñÊµèËßàÂô®Áõ¥Êé•ÁªëÂÆöÊªöÂä®‰∫ã‰ª∂
cvs.parentNode.onmousewheel = scrollFunc;
